    private void jButton8ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton8ActionPerformed

        ///SERVIDOR MULTICAST

        try {
            int Puerto = 12345;
            MulticastSocket ms = new MulticastSocket();

            //NUEVA VERSIÓN
            InetAddress mcastaddr = InetAddress.getByName("228.5.6.7");
            InetSocketAddress group = new InetSocketAddress(mcastaddr, Puerto);
            NetworkInterface netIf = NetworkInterface.getByName("bge0");
            ms.connect(new InetSocketAddress(mcastaddr, 12345));

            //VESRION ANTIGUA
            InetAddress grupo = InetAddress.getByName("225.0.0.1");
            ms.setInterface(InetAddress.getByName("10.2.9.100"));
            ms.joinGroup(grupo);

            String msg = "";
            while (!msg.trim().equals("*")) {
                byte[] buf = new byte[1000];
                DatagramPacket paquete = new DatagramPacket(buf, buf.length);
                ms.send(paquete);
                msg = new String(paquete.getData());
                System.out.println(msg.trim());
            }
            //NUEVA VERSIÓN
            ms.joinGroup(group, netIf);
            //VERSIÓN ANTIGUA
            ms.joinGroup(grupo);
            System.out.println("Socket cerrado...");
            ms.close();

        } catch (IOException e) {
            System.err.println("NO SE PUEDE CONECTAR AL SERVIDOR.");
            e.printStackTrace();
            System.exit(2);
        }
    }//GEN-LAST:event_jButton8ActionPerformed

