class ServidorTCP extends Thread {

    private String mensaje;

    public ServidorTCP(String mensaje) {
        this.mensaje = mensaje;

    }

    public void run() {
        try {
            ServerSocket serverSocket = new ServerSocket(7000);

            while (true) {
                Socket socket = serverSocket.accept();
                PrintWriter out = new PrintWriter(socket.getOutputStream(), true);
                out.println(mensaje);

                socket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }

    }
}

class clienteTcp extends Thread {

    private String host;
    private int puerto;
    private JTextField jTextField2;

    private JTextField jTextField8;

    public clienteTcp(String host, int puerto, JTextField jTextField2, JTextField jTextField8) {
        this.host = host;
        this.puerto = puerto;
        this.jTextField2 = jTextField2;
        this.jTextField8 = jTextField8;
    }

    public void run() {
        try {
            Socket socket = new Socket(host, puerto);
            BufferedReader in = new BufferedReader(new InputStreamReader(socket.getInputStream()));
            String mensaje = in.readLine();
            jTextField2.setText(mensaje);

            Persona p = new Persona("Juanito", "Frauca", "555555", "NoBinario");

            jTextField8.setText(p.getNombre() +" "+ p.getApellidos());


            socket.close();
        } catch (IOException e) {
            e.printStackTrace();


        }


    }


}


class Persona {

    String nombre;
    String apellidos;
    String DNI;
    String sexo;


    public Persona(String nombre, String apellidos, String DNI, String sexo) {
        this.nombre = nombre;
        this.apellidos = apellidos;
        this.DNI = DNI;
        this.sexo = sexo;

    }

    public String getNombre() {
        return nombre;
    }

    public String getApellidos() {
        return apellidos;
    }




}


